  by: ["userId"],
  where: { userId: { in: userIds } },
  _count: { _all: true },
  _sum: { total: true },
  _max: { createdAt: true },
}) as Array<{
  userId: string;
  _count: { _all: number };
  _sum: { total: any };
  _max: { createdAt: Date | null };
}>;

