  by: ["userId"],
  where: {
    userId: { in: userIds },
    status: { in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED] },
  },
  _count: { _all: true },
}) as Array<{
  userId: string;
  _count: { _all: number };
}>;

