export async function getRecommendedProducts() {
  try {
    await requiredUser();
    const id = await userId();

    if (!id) {
      throw new Error("User ID not found");
    }

    // First, try to get products based on customer's purchase history
    try {
      // Get customer's completed orders with items
      const customerOrders = await prisma.order.findMany({
        where: {
          userId: id,
          status: {
            in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED],
          },
        },
        select: {
          items: {
            select: {
              productId: true,
            },
          },
        },
      });

      // Extract product IDs from purchase history
      const purchasedProductIds = customerOrders
        .flatMap((order) => order.items)
        .map((item) => item.productId);

      if (purchasedProductIds.length > 0) {
        // Get categories of purchased products
        const purchasedProducts = await prisma.product.findMany({
          where: {
            id: { in: purchasedProductIds },
          },
          select: {
            categories: {
              select: {
                categoryId: true,
              },
            },
          },
        });

        const categoryIds = [
          ...new Set(
            purchasedProducts
              .flatMap((product) => product.categories)
              .map((cat) => cat.categoryId)
          ),
        ];

        // Get recommended products from same categories (excluding already purchased)
        if (categoryIds.length > 0) {
          const recommendedProducts = await prisma.product.findMany({
            where: {
              status: "ACTIVE",
              id: { notIn: purchasedProductIds },
              categories: {
                some: {
                  categoryId: { in: categoryIds },
                },
              },
            },
            select: {
              id: true,
              title: true,
              price: true,
              compareAtPrice: true,
              images: {
                select: {
                  url: true,
                  alt: true,
                },
                take: 1,
                orderBy: { sort: "asc" },
              },
            },
            take: 6,
            orderBy: { createdAt: "desc" },
          });

          if (recommendedProducts.length > 0) {
            return recommendedProducts;
          }
        }
      }
    } catch (error) {
      // If personalized recommendations fail, fall back to popular products
      logger.error("Error getting personalized recommendations, falling back to popular products");
    }

    // Fallback: Return popular/latest products
    const popularProducts = await prisma.product.findMany({
      where: {
        status: "ACTIVE",
      },
      select: {
        id: true,
        title: true,
        price: true,
        compareAtPrice: true,
        images: {
          select: {
            url: true,
            alt: true,
          },
          take: 1,
          orderBy: { sort: "asc" },
        },
      },
      take: 6,
      orderBy: { createdAt: "desc" },
    });

    return popularProducts;
  } catch (error) {
    logger.error(
      "Error fetching recommended products: " +
        (error instanceof Error ? error.message : String(error)),
    );
    throw new Error("Failed to fetch recommended products");
  }
}"}

