import { 
  ShoppingCart, 
  Clock, 
  CheckCircle2, 
  Users, 
  TrendingUp,
  Package
} from "lucide-react";

const [
  todayOrders,
  weeklyOrders, 
  monthlyOrders,
  todayRevenue,
  weeklyRevenue,
  monthlyRevenue,
  monthlyTotalOrders,
] = await Promise.all([
  // Today's orders count
  prisma.order.count({
    where: {
      createdAt: {
        gte: todayStart,
        lte: todayEnd,
      },
      status: { in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED] },
    },
  }),

  // Weekly orders count
  prisma.order.count({
    where: {
      createdAt: { gte: weekStart },
      status: { in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED] },
    },
  }),

  // Monthly orders count
  prisma.order.count({
    where: {
      createdAt: { gte: monthStart },
      status: { in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED] },
    },
  }),

  // Today's revenue
  prisma.order.aggregate({
    where: {
      createdAt: {
        gte: todayStart,
        lte: todayEnd,
      },
      status: { in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED] },
    },
    _sum: { total: true },
  }).then(result => Number(result._sum.total || 0)),

  // Weekly revenue
  prisma.order.aggregate({
    where: {
      createdAt: { gte: weekStart },
      status: { in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED] },
    },
    _sum: { total: true },
  }).then(result => Number(result._sum.total || 0)),

  // Monthly revenue
  prisma.order.aggregate({
    where: {
      createdAt: { gte: monthStart },
      status: { in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED] },
    },
    _sum: { total: true },
  }).then(result => Number(result._sum.total || 0)),

  // Total orders this month for processing rate calculation
  prisma.order.count({
    where: { createdAt: { gte: monthStart } },
  }),
]);

// Calculate processing rate (completed vs total orders this month)
const orderProcessingRate = monthlyTotalOrders > 0 
  ? Math.round((monthlyOrders / monthlyTotalOrders) * 100)
  : 100;

// Mock customer satisfaction (can be implemented with actual feedback system)
const customerSatisfaction = 92;

return {
  todayOrders,
  weeklyOrders,
  monthlyOrders,
  todayRevenue,
  weeklyRevenue,
  monthlyRevenue,
  orderProcessingRate,
  customerSatisfaction,
};

// Today's revenue
prisma.order.aggregate({
  where: {
    createdAt: {
      gte: todayStart,
      lte: todayEnd,
    },
    status: { in: [OrderStatus.DELIVERED, OrderStatus.CONFIRMED] },
  },
  _sum: { total: true },
}).then(result => Number(result._sum?.total || 0)),

// Weekly revenue
prisma.order.aggregate({
